export default {
  data() {
    return {
      type: "",
      subject: "",
      keyWords: "",
      tableData: [],
      type_arr: ["单项选择题", "多项选择题", "判断题", "简答题"],
      subject_obj: {
        yw: "语文",
        sx: "数学",
        yy: "英语",
        wl: "物理",
        hx: "化学",
        sw: "生物",
        ls: "历史",
        zz: "政治",
        dl: "地理",
        xx: "信息技术",
        xl: "心理",
        ys: "艺术",
        qt: "其他"
      },
      page_data: {
        size: 10,
        total: 0,
        current: 0
      },
      loading: true
    };
  },
  mounted() {
    this.getList();
  },
  methods: {
    handleDsp(str) {
      //处理描述的函数
      str = str.replace(/<[^>]+>/g, "");
      if (str.length > 40) return str.substr(0, 40) + "...";
      return str;
    },
    chooseType() {},
    strToJson(objStr) {
      //字符串转json
      return new Function("return " + objStr)();
    },
    editProblem(id) {
      this.$router.push({
        path: `/edit-problems/?id=${id}`
      });
    },
    getList() {
      this.loading = true;
      this.$axios
        .get(this.api, {
          params: {
            action: "getProblemsList",
            page: this.page_data.current,
            size: this.page_data.size
          }
        })
        .then(res => {
          let data = res.data.msg;
          for (var i in data) {
            data[i].options = this.strToJson(data[i].options);
          }
          this.tableData = data;
          this.page_data.total = Number(res.data.total);
          this.loading = false;
        })
        .catch(err => {
          console.log(err);
        });
    },
    pageChange(c) {
      this.page_data.current = c - 1;
      this.getList();
    }
  }
};
